sp_header_footer.client_script:
api.controller = function($window) {
  var c = this;

  c.logout = function() {
    var portalSuffix = "govsmart"; 
    var loginPageID = "govlogin"; 
    $window.location.href = "logout.do?sysparm_goto_url=/" + portalSuffix + "?id=" + loginPageID;
  };
};

