sp_header_footer.script:
(function() {
  data.isLoggedIn = gs.getSession().isLoggedIn();

  // بنجيب اسم الصفحة الحالية عشان نعرف إحنا فين
  data.page_id = $sp.getParameter('id');

  if (data.isLoggedIn) {
    data.userName = gs.getUserDisplayName();
    // ممكن مستقبلاً نجيب صورة اليوزر لو متاحة
    // data.userPhoto = ...
  }
})();
