sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   var serviceType = newValue; 
   var location = g_form.getValue('government');


   g_form.clearOptions('region');

   if (
      serviceType == 'national_id_card_application' ||
      serviceType == 'renew_the_national_id_card' ||
      serviceType == 'family_registration_extract'
   ) {

      if (location =='alexandria') {
         addAlexCivilRegistry();
      }

      if (location == 'cairo') {
         g_form.addOption('region', 'Civil Registry – Abbasia (Main HQ)', 'Civil Registry – Abbasia (Main HQ)');
      }
   }


   if (serviceType == 'renew_your_driver_s_license') {

      if (location == 'alexandria') {
         addAlexTrafficUnits();
      }

      if (location == 'cairo') {
         addCairoTrafficUnits();
      }
   }
}


function addAlexCivilRegistry() {
   g_form.addOption('region', 'Civil Registry – Sidi Gaber', 'Civil Registry – Sidi Gaber');
   g_form.addOption('region', 'Civil Registry – Ibrahimia', 'Civil Registry – Ibrahimia');
   g_form.addOption('region', 'Civil Registry – Mansheya', 'Civil Registry – Mansheya');
}

function addAlexTrafficUnits() {
   g_form.addOption('region', 'Abees Traffic Unit', 'Abees Traffic Unit');
   g_form.addOption('region', 'Montazah Traffic Unit', 'Montazah Traffic Unit');
   g_form.addOption('region', 'El Montazah Traffic Licenses Unit', 'El Montazah Traffic Licenses Unit');
}

function addCairoTrafficUnits() {
   g_form.addOption('region', 'Ain El Sira Traffic Office', 'Ain El Sira Traffic Office');
   g_form.addOption('region', 'Heliopolis Traffic Office', 'Heliopolis Traffic Office');
   g_form.addOption('region', 'New Nasr City Traffic Office', 'New Nasr City Traffic Office');

}
