sys_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
    if (g_form.getValue('approval') == 'approved') {
        g_form.setMandatory('assigned_to', true);
    } else {
        g_form.setMandatory('assigned_to', false);
    }

    // if (g_form.getValue("assigned_to") ) {
    //     g_form.setMandatory('document_status', true);
    // } else{
	// 	g_form.setMandatory('document_status', false);
	// }

    if (g_form.getValue('state') == 2) {
        g_form.setReadOnly("document_status", false);
        g_form.setReadOnly("description", false);
        g_form.setReadOnly("priority", false);
    } else {
        g_form.setReadOnly("document_status", true);
        g_form.setReadOnly("description", true);
        g_form.setReadOnly("priority", true);

    }
}
