(function executeRule(current, previous) {

    // make sure requester_data exists
    if (!current.requester_data) {
        return;
    }

    // load referenced record
    var req = new GlideRecord('x_1850706_govsma_0_government_service_request');
    if (!req.get(current.requester_data)) {
        return;
    }

    var serviceType = req.getValue('service_type');
    var governorate = req.getValue('government');

    // -------------------------
    // Civil Registry Services
    // -------------------------
    if (
        serviceType == 'family_registration_extract' ||
        serviceType == 'renew_the_national_id_card' ||
        serviceType == 'national_id_card_application'
    ) {
        if (governorate == 'cairo') {
            current.setValue('assignment_group', 'b635a5fd83f1b61078c11a30ceaad3e7');
        } else {
            current.setValue('assignment_group', '7c74e5fd83f1b61078c11a30ceaad364');
        }
    }
    // -------------------------
    // Driver's License Services
    // -------------------------
    else if (serviceType == 'renew_your_driver_s_license') {
        if (governorate == 'cairo') {
            current.setValue('assignment_group', '7185217d83f1b61078c11a30ceaad362');
        } else {
            current.setValue('assignment_group', '6b4561718335b61078c11a30ceaad32b');
        }
    }

})(current, previous);
