sys_script.script:
(function executeRule(current, previous) {

    gs.info(" WhatsApp START Ticket: " + current.number);

    var userGR = new GlideRecord('sys_user');

    if (userGR.get(current.opened_by.toString())) {

        var userNumber = userGR.getValue('mobile_phone') || userGR.getValue('phone');

        if (userNumber && userNumber.startsWith('+')) {

            var location = current.getDisplayValue('location') || 'N/A';
            var orderNumber = current.getValue('order_number') || current.number;
            var dateValue = current.getDisplayValue('date') || gs.nowDateTime();

            var messageText =
                "*Ticket Details*\n\n" +
                "*Location:* " + location + "\n" +
                "*Number:* " + orderNumber + "\n" +
                "*Date:* " + dateValue + "\n\n" +
                "Thank you for using our service";

            try {
                var rm = new sn_ws.RESTMessageV2('whatsapp sandbox', 'send whatsapp message');
                rm.setStringParameterNoEscape('from', 'whatsapp:+14155238886');
                rm.setStringParameterNoEscape('to', 'whatsapp:' + userNumber);
                rm.setStringParameterNoEscape('body', messageText);

                var response = rm.execute();
                gs.info("Twilio Status: " + response.getStatusCode());
                gs.info("Twilio Body: " + response.getBody());

            } catch (ex) {
                gs.error("WhatsApp error: " + ex.message);
            }

        } else {
            gs.error("Invalid or missing phone number for user: " + userGR.getDisplayValue());
        }

    } else {
        gs.error("User not found for ticket " + current.number);
    }

    gs.info("WhatsApp BR END ");

})(current, previous);
