sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // make sure requester_data exists
    if (!current.requester_data) {
        return;
    }

    // load referenced record
    var req = new GlideRecord('x_1850706_govsma_0_government_service_request');
    if (!req.get(current.requester_data)) {
        return;
    }
    var serviceType = req.getValue('service_type');
    var prefix = '';

    switch (serviceType) {
        case 'family_registration_extract':
            prefix = 'FRE';
            break;

        case 'renew_the_national_id_card':
            prefix = 'NID-R';
            break;

        case 'renew_your_driver_s_license':
            prefix = 'DL-R';
            break;

        case 'national_id_card_application':
            prefix = 'NID-A';
            break;

        default:
            prefix = '';
    }
    current.setValue('service_type_prefix',prefix);

})(current, previous);
