sys_script_include.script:
var GovsmaRecordUtils = Class.create();
GovsmaRecordUtils.prototype = {

    initialize: function () {},

   getValueByQuery: function (table, field, value, targetField) {
    var gr = new GlideRecord(table);
    gr.addQuery(field, value);
    gr.setLimit(1);
    gr.query();
    if (gr.next()) {
        return gr.getValue(targetField);
    }
    return '';
},

    updateValue: function (table, sysId, field, value) {
        var gr = new GlideRecord(table);
        if (gr.get(sysId)) {
            gr.setValue(field, value);
            gr.update();
            return true;
        }
        return false;
    },

    type: 'GovsmaRecordUtils'
};
